using UnityEngine;namespace SelectedEffectOutlineURP{	[RequireComponent(typeof(Camera))]	public class OutlineFilter : MonoBehaviour	{		[Header("Filter")]		public OutlineFeature m_Feature;		public LayerMask m_LayerMask;		OutlineFilterObj[] m_OutlineFilterObjs;		void Start()		{			m_Feature.SetActive(true);			m_OutlineFilterObjs = GameObject.FindObjectsOfType<OutlineFilterObj>();			for (int i = 0; i < m_OutlineFilterObjs.Length; i++)				m_OutlineFilterObjs[i].Initialize();		}		void OnDisable()		{			m_Feature.SetActive(false);		}	}}